import{notify as s}from"../utils/notify.js";export async function DownloadTasks(){const e=await(await fetch("/api/download")).blob(),o=document.createElement("a");o.setAttribute("download","export.json"),o.href=URL.createObjectURL(e),o.click()}export async function UploadTasks(t){let e=await fetch("/api/upload",{method:"POST",body:t});return e.ok?(s("Tasks were successfully imported","success"),!0):(console.error(e),s("Failed to import tasks","error"),!1)}
